import {
  BrowserRouter,
  Navigate,
  Route,
  Routes,
  useNavigate,
} from "react-router-dom";
import HomePage from "./homePage/HomePage";
import Login from "./login/Login";
import { useEffect } from "react";

function App() {
  const CheckToken = () => {
    const navigate = useNavigate();
    const token = localStorage.getItem("accessToken");

    useEffect(() => {
      if (!token) {
        navigate("/login", { replace: true }); // Redirect to the login page
      }
    }, [token, navigate]);

    return <Navigate to="/login" />; // Render the Navigate component for the authenticated route
  };
  // check ri là sai
  // lúc chạy app thì nó chạy cái route rồi

  {
    /* check ở đây, hiểu chưa - dạ  */
    /* check như em là nó đã chạy qua homepage rồi , xong em dùng event load nó mới đẩy qua login -> sai */
    /* check lúc render token ? <route homepage>: <route login */
    /* check useEffec nêwus ko có token thì đẩy qua */
  }
  return (
    <BrowserRouter>
      <Routes>
        {CheckToken()} ? <Route path="/HomePage" Component={HomePage} /> :
        <Route path="/login" Component={Login} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
