import {
  BrowserRouter,
  Navigate,
  Route,
  Routes,
  useNavigate,
} from "react-router-dom";
import HomePage from "./homePage/HomePage";
import Login from "./login/Login";
import { useEffect } from "react";

function App() {
  const token = localStorage.getItem("accessToken");

  // check ri là sai
  // lúc chạy app thì nó chạy cái route rồi
  useEffect(() => {
    const handleLoad = () => {
      // Thực hiện hành động sau khi trang chính được tải
      if (!token) {
        <Navigate to={"/login"} />;
      }
    };
    handleLoad();
  }, []);
  return (
    <BrowserRouter>
      <Routes>
        {/* check ở đây, hiểu chưa - dạ  */}
        {/* check như em là nó đã chạy qua homepage rồi , xong em dùng event load nó mới đẩy qua login -> sai */}
        {/* check lúc render token ? <route homepage>: <route login */}
        {/* check useEffec nêwus ko có token thì đẩy qua */}
        {useEffect()} ? <Route path="/login" Component={Login} /> :
        <Route path="/HomePage" Component={HomePage} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
